#!/usr/bin/env python3\nimport sys\ntry:\n    import qrcode\nexcept ImportError:\n    print('Install qrcode: pip install qrcode[pil]'); sys.exit(1)\nif len(sys.argv)<3:\n    print('Usage: python3 generate_qr.py <github-username> <repo-name>'); sys.exit(1)\nuser=sys.argv[1].strip(); repo=sys.argv[2].strip()\nbase=f'https://{user}.github.io/{repo}'\npages=['index.html','start.html','spegel.html','omstart.html','final.html']\nfor p in pages:\n    qr=qrcode.make(base + '/' + p)\n    qr.save(p.replace('.html','') + '_qr.png')\nprint('QR regenerated to', base)\n